package day4;

public class Ex10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i =0;
		while(i<5) {
			System.out.printf("들어오세요 %d 번님\n",i+1);
			i++;
		}
		
		System.out.println();
		
		for(int j = 0;j<5;j++) {
			System.out.printf("들어오세요 %d 번님\n",j+1);
		}
		
	}

}
